import { useEffect, useState } from 'react';
import './App.css';

function App() {

  const [data,setData]=useState([]);
  const [form,setForm]=useState({
    user:'',
    linkedinUrl:'',
    content:''
  })
  const saveItem =()=>{
    if(form.user === "" || form.linkedinUrl === "" || form.content ===""){ alert('Lütfen boş alanları doldurunuz!');return;}
    data.push({
      ...form
    });
    localStorage.setItem("DATA",JSON.stringify(data))
    setForm({
      user:"",
      linkedinUrl:"",
      content:""
    })
  }
  useEffect(()=>{
    getItem();
  },[]);
  const getItem =()=>{
    const data1 = localStorage.getItem('DATA') ?? [];
    setData(JSON.parse(data1));
  }
  return (
    <div className="App">
      <div className='form'>
        <div className='inputs'> 
          <input className='input' placeholder='İsim Soyisim' onChange={(event)=>setForm({...form,user:event.target.value})} value={form.user}/>
          <input className='input' placeholder='Linkedin linki' onChange={(event)=>setForm({...form,linkedinUrl:event.target.value})} value={form.linkedinUrl}/>
        </div>
        <textarea className='textinput' placeholder='Önerilerinizi Giriniz !' onChange={(event)=>setForm({...form,content:event.target.value})} value={form.content}/>
        <button className='button' onClick={saveItem}>Ekle</button>
      </div>
      <hr/>
      <div>
        {data.map((item) => (
          <div className='content-item'>
            <div>
            <a target='blank' href={item.linkedinUrl}>{item.user}</a>
            <p>{`"${item.content}"`}</p>
            </div>
            </div>
        ))}
      </div>
    </div>
  );
}

export default App;
