import { useState } from 'react';
import './App.css';

function App() {

  const [data,setData]=useState([
    {user:'Sercan Lülleci',linkedinUrl:'https://www.linkedin.com/in/sercan-l%C3%BClleci-673440125/',content:'Javascript iyi öğrenmelisin'},
    {user:'Burçin Lülleci',linkedinUrl:'https://www.linkedin.com/in/sercan-l%C3%BClleci-673440125/',content:'React için örnekler yapmalısın'},
    {user:'Merve Yüksel',linkedinUrl:'https://www.linkedin.com/in/sercan-l%C3%BClleci-673440125/',content:'Css için güçlenmelisin'}
  ]);
  const [form,setForm]=useState({
    user:'',
    linkedinUrl:'',
    content:''
  })
  return (
    <div className="App">
      <div className='form'>
        <div className='inputs'> 
          <input className='input' placeholder='İsim Soyisim' onChange={(event)=>setForm({...form,user:event.target.value})} value={form.user}/>
          <input className='input' placeholder='Linkedin linki' onChange={(event)=>setForm({...form,linkedinUrl:event.target.value})} value={form.linkedinUrl}/>
        </div>
        <textarea className='textinput' placeholder='Önerilerinizi Giriniz !' onChange={(event)=>setForm({...form,content:event.target.value})} value={form.content}/>
        <button className='button' onClick={saveIem}>Ekle</button>
      </div>
      <hr/>
      <div>
        {data.map((item) => (
          <div className='content-item'>
            <div>
            <a target='blank' href={item.linkedinUrl}>{item.user}</a>
            <p>{`"${item.content}"`}</p>
            </div>
            </div>
        ))}
      </div>
    </div>
  );
}

export default App;
