import { useState } from 'react';
import './App.css';

function App() {

  const [data,setData]=useState([
    {user:'Sercan Lülleci',linkedinUrl:'https://www.linkedin.com/in/sercan-l%C3%BClleci-673440125/',content:'Javascript iyi öğrenmelisin'},
    {user:'Burçin Lülleci',linkedinUrl:'https://www.linkedin.com/in/sercan-l%C3%BClleci-673440125/',content:'React için örnekler yapmalısın'},
    {user:'Merve Yüksel',linkedinUrl:'https://www.linkedin.com/in/sercan-l%C3%BClleci-673440125/',content:'Css için güçlenmelisin'}


  ]);
  return (
    <div className="App">
      <div className='form'>
        <div className='inputs'> 
          <input className='input' placeholder='İsim Soyisim'/>
          <input className='input' placeholder='Linkedin linki'/>
        </div>
        <textarea className='textinput' placeholder='Önerilerinizi Giriniz !'/>
        <button className='button'>Ekle</button>
      </div>
      <br/>
      {data.map((item) => (
        <div>
          <a target='blank' href={item.linkedinUrl}>{item.user}</a>
          </div>
      ))}
    </div>
  );
}

export default App;
